#include "largeprime.h"

bignum256* bignum_largeprime(void)
{
    static bignum256 prime;
    static bool initialized = false;
    if (!initialized) {
        // 2^256 - 188
        const uint8_t prime_bytes[32] = {
            0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff,
            0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff,
            0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0xff, 0x44,
        };
        bn_read_be(prime_bytes, &prime);
        initialized = true;
    }
    return &prime;
}
